(()=>{console.log("Mainjs Working");var t=document.getElementById("theme-toggle"),l=document.documentElement;localStorage.getItem("theme")==="dark"&&l.classList.add("dark");t.addEventListener("click",()=>{l.classList.toggle("dark"),l.classList.contains("dark")?(localStorage.setItem("theme","dark"),t.textContent="\u2600\uFE0F"):(localStorage.setItem("theme","light"),t.textContent="\u{1F319}")});var n=document.getElementById("menu-toggle"),s=document.getElementById("menu-close"),e=document.getElementById("mobile-menu");n.addEventListener("click",()=>{e.classList.remove("-translate-x-full"),e.classList.add("translate-x-0")});s.addEventListener("click",()=>{e.classList.remove("translate-x-0"),e.classList.add("-translate-x-full")});})();
